function getCuerpoTabla(datos, columnas) {
    var cuerpo=[];
    var encabezado=[{ text: 'Cdigo', style: 'encabezado' }, { text: 'Asignatura', style: 'encabezado' }, { text: 'Grupo', style: 'encabezado'}, { text: 'Horario', style: 'encabezado'}, { text: 'Intensidad semanal', style: 'encabezado'}];
    cuerpo.push(encabezado);
    datos.forEach(function(fila) {
          var datoFila = [];
          columnas.forEach(function(columna) {
              datoFila.push(fila[columna].toString());
          })
          cuerpo.push(datoFila);
    });
    return cuerpo;
}

function getTabla(datos, columnas) {
    return {
      style: 'tabla',
        table: {
            headerRows: 1,
            body: getCuerpoTabla(datos, columnas),
            alignment: 'center'
        }
    };
}

