function generarInformacion(elemento){

	var dedicacionesEspecificas=elemento.dedicacionesEspecificas;

	var docente=elemento.docente;
	var asignaturas=elemento.asignaturas;
	var grupos=elemento.grupos;
	var gruposdocentes=elemento.gruposdocentes;


	var aux=[];

  		gruposdocentes.forEach(function(grupodocente){
  			if(docente.Id==grupodocente.IdDocente.Id){
  				grupos.forEach(function(grupo){
  					if(grupo.Id==grupodocente.IdGrupo.Id){
  						var nombre;
				  		var grupoid;
				  		var horario;
				  		var intensidad;
				  		var codigo;
  						asignaturas.forEach(function(asignatura){
  							if(asignatura.Id==grupo.IdAsignatura.Id){
  								nombre=asignatura.Nombre;
  								codigo=asignatura.Id;
  							};
  						});
  						grupoid=grupo.Id;
  						horario=grupo.Horario;
  						intensidad=grupo.HorasSemanales;
  						var elemento = {
  							Codigo: codigo,
				  			Nombre: nombre,
				  			Grupo: grupoid,
				  			Horario: horario,
				  			Intensidad: intensidad
				  		};
				  		aux.push(elemento);
  					};
  				});
  			};
  		});
	
	return aux;
}

