getContenido=function(elemento){
  	var contenido = [];
  	var docente=elemento.docente;
  	contenido.push({ text: 'FORMATO CARGA ACADÉMICA No '+1,
	    style: 'titulo'});
  	elemento.funcionarios.forEach(function(funcionario){
  		if(funcionario.Id==docente.IdFuncionario.Id){
  			contenido.push({ text: 'DOCENTE '+funcionario.PrimerNombre+' '+funcionario.SegundoNombre+' '+funcionario.PrimerApellido+' '+funcionario.SegundoApellido,
        		style: 'titulo'});
  		}
  	});
  	contenido.push({ text: 'ASIGNATURAS',
        style: 'titulo'});
  	contenido.push(getTabla(generarInformacion(elemento), ['Codigo', 'Nombre', 'Grupo', 'Horario', 'Intensidad']));
  	contenido.push({ text: 'PERIODO 2017-1',
        style: 'titulo'});
  	return contenido;
};

